var LocationsViewModel=function(){var self=this;self.locationFilter=ko.observable('');self.locationMarkers=[];var i=0;locations.forEach(function(loc){var marker=createMarker(loc);addMarkerListeners(marker);self.locationMarkers.push(marker);i++;});self.locationClicked=function(marker){markerClicked(marker);};self.filteredLocationMarkers=ko.computed(function(){var filter=self.locationFilter().toLowerCase();if(!filter){self.locationMarkers.forEach(function(marker){showMarker(marker);});return self.locationMarkers;} else{var filteredMarkers=[];self.locationMarkers.forEach(function(marker){if(marker.title.toLowerCase().indexOf(filter)!=-1){showMarker(marker);filteredMarkers.push(marker);} else{hideMarker(marker);}});return filteredMarkers;}},self);};var leftNavigationVisible=false;var toggleLeftNavigation=function(){leftNavigationVisible=!leftNavigationVisible;if(leftNavigationVisible){document.getElementById("locationlist-leftnavigation").style.width="260px";} else{document.getElementById("locationlist-leftnavigation").style.width="0";}};